import { Template } from 'meteor/templating';
import { ReactiveVar } from 'meteor/reactive-var';
import { Meteor } from 'meteor/meteor';

import './main.html';

Meteor.startup(() => {
		Meteor.call('removeAllPosts');
		Devices.insert({name: "device1", state: "Started"});
		Devices.insert({name: "device2", state: "Started"});
		Devices.insert({name: "device3", state: "Not started"});
		Devices.insert({name: "device4", state: "Not started"});
		Devices.insert({name: "device5", state: "Initializing"});
});

var deviceList_Selected = "";
Template.deviceList.helpers({
	'devices': function(){
		return Devices.find({});
	},
	'isStarted': function (state) {
		return state === "Started";
	},
	'isWaiting': function (state) {
		return state === "Initializing";
	},
	'isClosed': function (state) {
		return state === "Not started";
	},
	'isSelected': function (name) {
		return name === deviceList_Selected;
	}
});
Template.deviceList.events({
  'click .ONdevice': function(event){
		console.log("ON");
		console.log(event.target.getAttribute('data-targetname'));
	},
	'click .OFFdevice': function(event){
		console.log("OFF");
		console.log(event.target.getAttribute('data-targetname'));
	},
	'click .INFOdevice': function(event){
		deviceList_Selected = event.target.getAttribute('data-targetname');console.log("OFF");console.log(deviceList_Selected);
	}
});
