import { Template } from 'meteor/templating';
import { ReactiveVar } from 'meteor/reactive-var';

import './main.html';

Meteor.startup(() => {
		Meteor.call('removeAllPosts');
		Devices.insert({name: "device1", state: "Started"});
		Devices.insert({name: "device2", state: "Started"});
		Devices.insert({name: "device3", state: "Not started"});
		Devices.insert({name: "device4", state: "Not started"});
		Devices.insert({name: "device5", state: "Initializing"});
});

Template.deviceList.helpers({
	'devices': function(){
		return Devices.find({});
	},
	'putCanc': function (state) {
		return state !== "Started"
	}
});
